async def select_schedule(
    executor: edgedb.AsyncIOExecutor,
    *,
    group_id: uuid.UUID,
    seminar_ids: list[uuid.UUID],
    date: datetime.date,
) -> list[SelectScheduleResult]:
    return await executor.query(
        """\
        WITH
          `group` := (
            SELECT `Group`
            FILTER .id = <uuid>$group_id
          ),
          seminars := (
            SELECT Seminar
            FILTER .id = array_unpack(<array<uuid>>$seminar_ids)
          ),
          schedule := (
            SELECT SeminarSchedule
            FILTER
              .`group` = `group` AND
              .seminars = seminars AND
              .date = <cal::local_date>$date
          )

        SELECT (
          schedule
        ) { ** };\
        """,
        group_id=group_id,
        seminar_ids=seminar_ids,
        date=date,
    )


async def update_or_insert_schedule(
    executor: edgedb.AsyncIOExecutor,
    *,
    group_id: uuid.UUID,
    seminar_ids: list[uuid.UUID],
    date: datetime.date,
) -> list[UpdateOrInsertScheduleResult]:
    return await executor.query(
        """\
        WITH
          group_obj := (
            SELECT `Group`
            FILTER .id = <uuid>$group_id
          ),
          seminars := (
            SELECT Seminar
            FILTER .id IN array_unpack(<array<uuid>>$seminar_ids)
          ),
          schedule := (
            SELECT SeminarSchedule
            FILTER
              .`group` = group_obj AND
              .date = <cal::local_date>$date
          )

        SELECT (
          IF EXISTS schedule THEN
            (
              UPDATE SeminarSchedule
              FILTER .id = schedule.id
              SET {
                seminars := seminars,
              }
            )
          ELSE (
            INSERT SeminarSchedule {
              `group` := group_obj,
              seminars := seminars,
              date := <cal::local_date>$date,
            }
          )
        ) { id };\
        """,
        group_id=group_id,
        seminar_ids=seminar_ids,
        date=date,
    )


